import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import GoogleMapsApiLoader from "google-maps-api-loader";

const useGoogleMap = apiKey => {
  const [googleMap, setGoogleMap] = useState(null);
  useEffect(() => {
    GoogleMapsApiLoader({ apiKey }).then(google => {
      setGoogleMap(google);
    });
  }, []);
  return googleMap;
};

const markerspos = [{lat: 35.6432027, lng: 139.6729435}, {lat: 35.6332027, lng: 139.6729435}];

const useMap = ({ googleMap, mapContainerRef, initialConfig }) => {
  const [map, setMap] = useState(null);
  useEffect(
    () => {
      if (!googleMap || !mapContainerRef.current) {
        return;
      }

      const map = new googleMap.maps.Map(
        mapContainerRef.current,
        initialConfig
      );

      const infoMarker = {
        fillColor: "blue",
        fillOpacity: 0.6,
        strokeWeight: 0,
        rotation: 0,
        scale: 2,
        anchor: new googleMap.maps.Point(15, 30),
      };

      // marker
      markerspos.map((center) => {
        const marker = new googleMap.maps.Marker({
          position: center,
          map: map,
          icon: infoMarker
        });
        const InfoWindow = new googleMap.maps.InfoWindow({
        content: `<div id="content">
                      <button id="onBtn" class="btn btn-sm">
                        Hola
                      </button>
                    </div>`
        });
        marker.addListener("click", () => {
          InfoWindow.open(map, marker);
        });
      })


      setMap(map);
    },
    [googleMap, mapContainerRef]
  );
  return map;
};

export { useGoogleMap, useMap };
